<template>
  <div>leetcode76</div>
</template>

<script>
export default {
  name: 'leetcode76',
  created() {
    // leetcode 76
    let s = "",
    t = "";
    // let s = "ab", t = "a";
    console.log("s.length :>> ", s.length);
    console.log("t.length :>> ", t.length);
    this.minWindow(s, t); //// {#} -sign -i -from -time -t created
    
  },
  methods: {
    // leetcode 76 滑动窗口法
    minWindow: function (s, t) {
      // 初始化数据
      // 创建左右指针，和结果字符串，创建字典表 如： aba -> {a: 2, b: 1}
      let l = 0,
        r = 0,
        res = "";
      const m = new Map();
      for (let i = 0; i < t.length; i++) {
        const c = t[i];
        m.set(c, m.has(c) ? m.get(c) + 1 : 1);
      }
      let needType = m.size;
      // 开始遍历字符串，录找结果
      while (r < s.length) {
        // 移动右指针，录找结果，
        // 如果字符是否目标串，种类数量 - 1，种类数量为0，种类 - 1，接近结果
        const c = s[r];
        if (m.has(c)) {
          m.set(c, m.get(c) - 1);
          if (m.get(c) === 0) needType -= 1;
        }
        r++;
        // 需要匹配的种类为0，找到一个结果，开始优化
        while (needType === 0) {
          // 当前结果如果比原先的结果更短，赋值
          let newRes = s.slice(l, r);
          if (!res || newRes.length < res.length) res = newRes;

          // 移动左指针，优化结果
          // 如果字符在目标串中，种类数量 + 1，种类数量不为0，种类 + 1，结果无效，重新寻找
          const c2 = s[l];
          if (m.has(c2)) {
            m.set(c2, m.get(c2) + 1);
            if (m.get(c2) === 1) needType += 1;
          }
          l++;
        }
      }
      // 返回结果值
      return res;
    },
  }
}
</script>

